// grab data for the specified parameters from the API
const grabData = async (url) => {
  const response = await fetch(url);
  const courses = await response.json();
  return courses["data"];
}

const createRatingTag = async () => {
  // take the semester for which data is being fetched and supply it to the API
  let semester = document.getElementsByClassName("select2-chosen")[0].innerHTML;
  let courses = await grabData("https://recourseatashoka.herokuapp.com/api/course?compound_score=0.1");

  // grab the rows of course data from AMS
  let rows = document.getElementById("tblListView").getElementsByTagName("tbody")[0].getElementsByTagName("tr");

  for (let row of rows) {

    // take data from the courses displayed on AMS
    let courseCell = row.getElementsByTagName("td")[2];
    let course_name = courseCell.innerHTML.trim().split("<")[0];
    let course_code = (row.getElementsByTagName("td")[3].innerHTML).split("/")[0];

    // get an array of every prof teaching the course in the row
    let course_teachers = []
    for (let email of (row.getElementsByTagName("td")[4].innerHTML).split(",")) {
      if (!email.includes("_")) {
        course_teachers.push(email.trim());
      }
    };

    for (let course of courses) {

      // get an array of every prof of the course
      let professors = [];
      for (let prof of course.faculty.professors) {
        professors.push(prof["email"]);
      }

      // if the API entry is of the same course as the row data
      if ((course.name.includes(course_name) || course_name.includes(course.name)) && professors.every((item)=>course_teachers.includes(item))) {

        // represent compound score to two decimals
        let compound_score = (course.ratings.compound_score).toFixed(2);

        // determine bubble colour based on rating
        let bubbleColor = "";
        let ratingColorMap = ["#8c0101", "#cf2402", "#e6a902", "#dbc902", "#a9db02", "#6fdb02", "#30b002", "#018c0b"];

        for (let color_index = 0; color_index <= 7; color_index++) {
          if ((compound_score*2) < (color_index+3)) {
            bubbleColor = ratingColorMap[color_index];
            break;
          }
        }

        // create rating bubble, add tilde if sample size is small
        ratingBubble = "";
        if (course.ratings.sample_size > 5) {
          ratingBubble = "<span style=\"padding-left: 10px; padding-right: 10px; margin-left: 10px; border-radius: 4px; background-color: " + bubbleColor + "; position: relative;\">" + compound_score + "</span>";
        } else {
          ratingBubble = "<span style=\"padding-left: 10px; padding-right: 10px; margin-left: 10px; border-radius: 4px; background-color: " + bubbleColor + "; color: white !important; position: relative;\">~" + compound_score + "</span>";
        }

        // create rating tag with link to API
        let ratingTag = "<a style=\"color: white; text-decoration: none;\" href=\"https://recourseatashoka.herokuapp.com/api/course?name=" + course_name + "&semester=" + semester + "\">" + ratingBubble + "</a>"
        
        // add tag to cell
        courseCell.innerHTML += ratingTag;
        break;
      }
      // if the API entry is of the same course as the row data
      if ((course.name.includes(course_name) || course_name.includes(course.name)) && professors.some((item)=>course_teachers.includes(item))) {

        // represent compound score to two decimals
        let compound_score = (course.ratings.compound_score).toFixed(2);

        // determine bubble colour based on rating
        let bubbleColor = "";
        let ratingColorMap = ["#8c0101", "#cf2402", "#e6a902", "#dbc902", "#a9db02", "#6fdb02", "#30b002", "#018c0b"];

        for (let color_index = 0; color_index <= 7; color_index++) {
          if ((compound_score*2) < (color_index+3)) {
            bubbleColor = ratingColorMap[color_index];
            break;
          }
        }

        // create rating bubble, add tilde if sample size is small
        ratingBubble = "";
        if (course.ratings.sample_size > 5) {
          ratingBubble = "<span style=\"padding-left: 10px; padding-right: 10px; margin-left: 10px; border-radius: 4px; background-color: " + bubbleColor + "; position: relative;\">" + compound_score + "</span>";
        } else {
          ratingBubble = "<span style=\"padding-left: 10px; padding-right: 10px; margin-left: 10px; border-radius: 4px; background-color: " + bubbleColor + "; color: white !important; position: relative;\">~" + compound_score + "</span>";
        }

        // create rating tag with link to API
        let ratingTag = "<a style=\"color: white; text-decoration: none;\" href=\"https://recourseatashoka.herokuapp.com/api/course?name=" + course_name + "&semester=" + semester + "\">" + ratingBubble + "</a>"
        
        // add tag to cell
        courseCell.innerHTML = course_name + " " + ratingTag;
        break;
      }
    }
  }
  createGradeTag();
}

